    public LauncherGUI() {
        initComponents();
        ver.setText(meta.getFullVersionInformation());
        rand.setText(createRandomSplash());
        newInfoText("XenLauncher Ready. Created by Benjamin Gwynn.");
        updateRepositoryInformation(repositorySelector.getSelectedItem().toString());
        repoBranchField.setText(meta.getVersionName().toLowerCase());
    }
    
    final void updateRepositorySelectorList() {
        Output.print('I', "Updating Repository Selector list...");
        repositorySelector.addItem(RepositoryDatabase.selectRepo(RepositoryDatabase.getListOfRepoNames().size() - 1));
        repositorySelector.revalidate();
        repositorySelector.updateUI();
    }
    
    private void updateRepositoryInformation(String repo_name) {
        repoInformationText.setText(null);
        try {
            List<String> line = RepositoryDatabase.getRepositoryMeta(repo_name, "repo_information");
            for (int i = 0; i < line.size(); i++) {
                repoInformationText.setText(repoInformationText.getText() + line.get(i) + "\n");
            }
        } catch (FileNotFoundException ex) {
            Output.error("Cannot get information. Information missing. (Java FileNotFoundException)", ex);
        } catch (IOException ex) {
            Output.error("Cannot get information. (Java IOException)", ex);
        }
    }
    
    public void setProgressBar(int percentage) {
        primaryProgressBar.setValue(primaryProgressBar.getWidth() * (percentage / 100));
        primaryProgressBar.updateUI();
    }
    
    public final void newInfoText(String text) {
        info_text.setText(text);
        info_text.updateUI();
    }
    
    public void showError(String msg) {
        //if (errorDialog.isVisible()) {
        //    errorDialog.setVisible(false);
        //}
        errorDialogErrorText.setText(msg);
        errorDialog.setVisible(true);
    }
    
    public void writeToLog(char type, String msg) {
        if ('E' == type) {
            switchToErrorTab();
        }
        if (guiLogText.getText().isEmpty()) {
            guiLogText.setText(msg + "\n");
        } else {
            guiLogText.setText(guiLogText.getText() + "\n" + msg + "\n");
        }
        guiLogText.updateUI();
    }
    
    private void switchToErrorTab() {
        this.switchTab(this.errorReportContainer);
    }
    
    private void switchTab(Component tab) {
        launcherTabs.setSelectedComponent(tab);
        launcherTabs.requestFocus();
        tab.requestFocus();
    }
    
    private static String createRandomSplash() {
        ArrayList splashList = new ArrayList();
        splashList.add("Written in Java!");
        splashList.add("Python-free!");
        splashList.add("Free to use!");
        splashList.add("Turtles!");
        splashList.add("Skeletons are OP.");
        splashList.add("Death by Java.");
        splashList.add("A work-in-progress.");
        splashList.add("Oops, typo.");
        splashList.add("Random text!");
        splashList.add("Spooky scary skeletons.");
        splashList.add("Nyan Cats!");
        splashList.add("Speedy thing goes in, speedy thing comes out.");
        splashList.add("Make it rain!");
        splashList.add("Open Source all the things!");
        splashList.add("Git powered!");
        splashList.add("1.7 Compatible!");
        splashList.add(String.valueOf(splashList.size() + 1) + " possible splashes!");
        return String.valueOf(splashList.get(new Random().nextInt(splashList.size())));
    }

