    private void repositorySelectorActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_repositorySelectorActionPerformed
        updateRepositoryInformation(repositorySelector.getSelectedItem().toString());
        if (repositorySelector.getSelectedItem().equals("XenLauncher Default Repository")) {
            removeRepoButton.setEnabled(false);
        } else {
            removeRepoButton.setEnabled(true);
        }
    }//GEN-LAST:event_repositorySelectorActionPerformed

    private void addRepoButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_addRepoButtonActionPerformed
        try {
            RepositoryDatabase.addRepo(repoNameField.getText(), repoURLField.getText());
            updateRepositorySelectorList();
        } catch (IOException ex) {
            Output.error("Cannot create the repo. Maybe you don't have permission to write here? (Java IOException)", ex);
        } catch (JGitInternalException ex) {
            Output.error("Cannot create the repo. Maybe the repo already exists but XenLauncher didn't see it? (Java JGitInternelException)", ex);
        } catch (RefNotFoundException ex) {
            Output.error("Cannot create the repo. Reference not found. Outdated repo? (Java RefNotFoundException)", ex);
            RepositoryDatabase.removeRepositoryFolderCatch(repoNameField.getText());
        } catch (InvalidRefNameException ex) {
            Output.error("Cannot create the repo. Not a Git repo. (Java InvalidRefNameException)", ex);
            RepositoryDatabase.removeRepositoryFolderCatch(repoNameField.getText());
        } catch (GitAPIException ex) {
            Output.error("Cannot create the repo. API Error. Bad Git repo? (Java GitAPIException)", ex);
            RepositoryDatabase.removeRepositoryFolderCatch(repoNameField.getText());
        }
        addRepoDialog.setVisible(false);
    }//GEN-LAST:event_addRepoButtonActionPerformed

    private void removeRepoButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_removeRepoButtonActionPerformed
        try {
            RepositoryDatabase.removeRepository(repositorySelector.getSelectedItem().toString());
            repositorySelector.removeItem(repositorySelector.getSelectedItem());
            repositorySelector.setSelectedIndex(0);
        } catch (Exception ex) {
            Output.error("Coudln't remove repository. (Vague Java exception)", ex);
        }
    }//GEN-LAST:event_removeRepoButtonActionPerformed

